import React, { useState } from 'react'
import { View, TextInput, Image, StyleSheet, Button, Alert } from 'react-native'
import firebase from 'firebase/compat/app';
require("firebase/compat/firestore")
require('firebase/compat/storage')

export default function Save(props) {
    const [caption, setCaption] = useState("");

    const uploadImage = async () => {
        const uri = "https://www.pngmart.com/files/2/Mario-PNG-Image.png";

        let filename = uri.substring(uri.lastIndexOf('/') + 1);
        console.log(uri)
        fetch(uri).then(res => {
            return res.blob();
        }).then(blob => {
            //uploading blob to firebase storage
            firebase.storage().ref().child(filename).put(blob).then(function (snapshot) {
                return snapshot.ref.getDownloadURL()
            }).then(url => {
                console.log("Firebase storage image uploaded : ", url);
            })
        }).catch(error => {
            console.error(error);
        })
    }


    return (
        <View style={styles.container}>
            <Image source={{ uri: props.route.params.image }} style={styles.image} />
            <TextInput placeholder='Write a Caption...' onChangeText={(caption) => setCaption(caption)} />
            <Button title='Send Post' onPress={() => uploadImage()} />
        </View>
    )
}

const styles = StyleSheet.create({
    container: {
        flex: 1,
    },
    image: {
        flex: 1,
    }
})
